// SPDX-License-Identifier: BUSL-1.1

pragma solidity ^0.8.0;

import "hardhat/console.sol";
import "../onft721Distribute/DistributeCore.sol";

/// @title Interface of the DistributeONFT721 standard
contract DistributeONFT721 is DistributeCore {

    TokenDistribute[] private tokenDistribute;

    /// @notice Constructor for the DistributeONFT721
    /// @param _name the name of the token
    /// @param _symbol the token symbol
    /// @param _layerZeroEndpoint handles message transmission across chains
    /// @param _index to set to all ones representing token ids available to mint
    constructor(string memory _name, string memory _symbol, address _layerZeroEndpoint, uint[] memory _index, uint _value) DistributeCore(_name, _symbol, _layerZeroEndpoint, _index, _value) {}

//    function countAllSetBits() public returns (uint count) {
//        for(uint i = 0; i < tokenIds.length; i++) {
//            count += BitMath.countSetBits(tokenIds[i]);
//        }
//        return count;
//    }

    function distributeTokens(uint16 _dstChainId, uint _amount, address payable _refundAddress, address _zroPaymentAddress, bytes memory _adapterParams) external payable onlyOwner {
//        verifyAmounts()

        bytes memory payload = getDistributeTokensPayload(_amount);
        if (useCustomAdapterParams) {
            _checkGasLimit(_dstChainId, FUNCTION_TYPE_SEND, _adapterParams, NO_EXTRA_GAS);
        } else {
            require(_adapterParams.length == 0, "LzApp: _adapterParams must be empty.");
        }
        _lzSend(_dstChainId, payload, _refundAddress, _zroPaymentAddress, _adapterParams, msg.value);
    }

    function getDistributeTokensPayload(uint _amount) public returns (bytes memory) {
        require(countAllSetBits() >= _amount, "Not enough tokens to distribute");
        uint amountNeeded = _amount;
        uint tokenIdsLength = tokenIds.length;
//        TokenDistribute[] memory tokenDistribute;
        delete tokenDistribute;
        for(uint i = 0; i < tokenIdsLength; i++) {
            if(tokenIds[i] == 0) continue;

            if(amountNeeded == 0) break;
            uint sendBytes;
            uint pos;
            while(amountNeeded != 0) {
                pos = BitMath.mostSignificantBit(tokenIds[i]);
                if(pos == 0) break;
                uint temp = 1 << pos;
                tokenIds[i] = tokenIds[i] ^ temp;
                sendBytes = sendBytes | temp;
                amountNeeded -= 1;
                if(tokenIds[i] == 0) break;
            }
            if(pos != 0) tokenDistribute.push(TokenDistribute(i, sendBytes));
        }
        return abi.encode(FUNCTION_TYPE_DISTRIBUTE, tokenDistribute);
    }
}

